import React, { useState, useEffect } from 'react';
import axios from 'axios'; //부모
import MovieCard from './MovieCard';
import { Pagination } from 'react-bootstrap';

const MovieListPaging = () => {
  const [movies, setMovies] = useState([]);

  //현재 페이지
  const [currentPage, setCurrentPage] = useState([]);

  //한 페이지 당 보여줄 영화 수
  const [moviesPerPage, setMoviesPerPage] = useState([]);

  //현재 페이지에서 영화 목록 계산
  const indexOfLastMovie = currentPage * moviesPerPage;
  const indexOfFirstMovie = indexOfLastMovie - moviesPerPage;
  const currentMovies = movies.slice(indexOfFirstMovie, indexOfLastMovie);

  //외부에서 API를 통해 영화 데이터를 가져오고 내가 원하는 내용만 보여주고싶다 => then에 요청.
  useEffect(() => {
    axios
      .get('https://yts.mx/api/v2/list_movies.json')
      .then((response) => {
        //가져온 데이터 중 필요한 정보만 가져와서 업데이트
        const movieData = response.data.data.movies.map((movie) => ({
          id: movie.id,
          title: movie.title,
          rating: movie.rating,
          poster: movie.medium_cover_image,
        }));
        setMovies(movieData);
      })
      .catch((error) => {
        console.log('데이터 없음', error);
      });
  }, []);

  //페이지를 변경하기 위한 핸들러 추가
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      {currentMovies.map((movie) => (
        <MovieCard key={movie.id} movie={movie} />
      ))}
      <Pagination>
        {[...Array(Math.ceil(movies.length / moviesPerPage)).keys()].map(
          (number) => (
            <Pagination.Item
              key={number + 1}
              active={number + 1 === currentPage}
              onClick={() => handlePageChange(number + 1)}
            ></Pagination.Item>
          )
        )}
      </Pagination>
    </div>
  );
};

export default MovieListPaging;
