import { useState } from 'react';

const NumberGuessingGame = () => {
  const randomNumber = () => {
    return Math.floor(Math.random() * 50) + 1; //0~49 이므로 +1 을 붙여줌
  };

  //targetNumber 에 랜덤으로 생성된 숫자값이 들어갈 수 있도록 설정
  const [targetNumber, setTargetNumber] = useState(randomNumber());
  const [userGuess, setUserGuess] = useState('');
  const [message, setMessage] = useState('');

  //숫자값이 들어올 때 마다 값 변경
  const inputChange = (event) => {
    setUserGuess(event.target.value);
  };

  //숫자값 전달
  const inputSubmit = (event) => {
    //페이지가 다시 로딩되는 것(자동으로 새로고침 되는 것)을 방지
    event.prevenDefault();

    //사용자가 입력한 값을 숫자로 변환해주는 역할
    const guess = parseInt(userGuess, 10); //10진수 : 우리가 흔히 사용하는 0~9 숫자를 의미

    //NaN : Not a Number. 주어진 값이 숫자가 맞는지 판별하는 역할
    if (isNaN(guess)) {
      setMessage('숫자만 입력이 가능합니다.');
    } else {
      if (guess === targetNumber) {
        setMessage(`${targetNumber}는 정답입니다~`);
        setTargetNumber(randomNumber);
      } else {
        setMessage(
          guess < targetNumber
            ? '숫자가 정답보다 낮습니다.'
            : '숫자가 정답보다 높습니다.'
        );
      }
      //input값 알아서 지워질 수 있도록 초기화 설정
      setUserGuess('');
    }
  };

  return (
    <div>
      <h1>숫자 맞추기 게임</h1>
      <p>1 부터 100 사이 숫자 맞추기</p>
      <form onSubmit={inputSubmit}>
        <input
          type="number"
          value={userGuess}
          onChange={inputChange}
          placeholder="숫자를 입력하세요."
          min="1"
          max="100"
          required
        />
        <button type="submit">제출하기</button>
      </form>
      {message && <div>{message}</div>}
    </div>
  );
};
export default NumberGuessingGame;
